mapper.select:
SELECT

FROM charge_type_dict a

WHERE a.id = #{id}

SELECT

FROM charge_type_dict a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM charge_type_dict a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE charge_type_dict SET 
	del_flag = '1'
WHERE id = #{id}
