mapper.select:
SELECT
*
FROM org_staff a

WHERE a.persion_id = #{0}
AND a.org_id = #{1}
AND a.del_flag = '0'

SELECT p.id,p.name,p.sex,p.card_no,p.phone_num,p.email,p.nick_name,p.nation,o.title,o.id as staffId FROM org_staff o,persion_info p WHERE o.persion_id=p.id
and o.dept_id=#{deptId}
and o.org_id=#{orgId} and o.persion_id=p.id and o.del_flag='0' and p.del_flag='0'

SELECT

FROM org_staff a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

