$(function() {
    $(".sidebar-collapse a").on("click",
    function() {
        $("#navigation").toggleClass("collapsed");
        if ($("#navigation").hasClass("collapsed")) {
           $("#mainContent").attr("style","height: 100%; margin-left:40px;");
            $("#navigation li a").tooltip({
                placement: "right",
                trigger: "hover",
                html: true,
                container: "body"
            });
            if ($(window).width() < 768) {
                $("#content").animate({
                    left: "0px"
                },
                150)
            } else {
                $("#content").animate({
                    paddingLeft: "55px"
                },
                150)
            }
        } else {
            $("#mainContent").attr("style","height: 100%; margin-left:150px;");
            $("#navigation li a").tooltip("destroy");
            if ($(window).width() < 768) {
                $("#content").animate({
                    left: "210px"
                },
                150)
            } else {
                $("#content").animate({
                    paddingLeft: "155px"
                },
                150)
            }
        }
    });


    $(".dropdown-toggle").on("click",
        function(e) {
            if($(this).parent().attr("class")!='dropdown open') {
                $(".dropdown-toggle").parent().attr("class", "dropdown");
                $(this).parent().addClass("dropdown open")
            }else{
                $(this).parent().attr("class", "dropdown");
            }



        }
    )

});
